import { useState } from "react";
import { WaterChart, CaloriesChart, StepsChart, SleepChart, DistanceChart } from "../components/Charts/fitnessCharts.js";
import { SugarChart, BPChart, HeartRateChart, CholesterolChart, OxygenChart } from "../components/Charts/MedicalCharts.js";
import WorkoutsChart from "../components/Charts/WorkoutsChart.js";

export default function Dashboard() {
  // Step 1: state for dynamic filter
  const [days, setDays] = useState(14);

  return (
    <div className="container">
      <div className="row">
        <h1 className="fs-2 mt-5">Welcome to Dashboard</h1>

        {/* Filter Buttons */}
        <div className="mb-3">
          <button 
            className={`btn me-2 ${days === 7 ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setDays(7)}
          >
            Last 7 Days
          </button>
          <button 
            className={`btn me-2 ${days === 14 ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setDays(14)}
          >
            Last 14 Days
          </button>
          <button 
            className={`btn ${days === 30 ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setDays(30)}
          >
            Last 30 Days
          </button>
        </div>

        {/* Charts */}
        <div className="col-12 col-md-6 col-lg-4 mt-3">
          <WaterChart days={days} />
          <CaloriesChart days={days} />
          <StepsChart days={days} />
          <WorkoutsChart days={days} goal={60}/>
        </div>

        <div className="col-12 col-md-6 col-lg-4 mt-3">
          <SleepChart days={days} />
          <DistanceChart days={days}/>
          <SugarChart days={days} />
          <BPChart days={days} />
        </div>

        <div className="col-12 col-md-6 col-lg-4 mt-3">
          <HeartRateChart days={days} />
          <CholesterolChart days={days} />
          <OxygenChart days={days} />
        </div>
      </div>
    </div>
  );
}
