import React from "react";
import { Link } from "react-router-dom";

function ParticleBackground() {
  return (
    <div className="relative w-full h-screen overflow-hidden bg-gray-50">
      {/* Particles */}
      {[...Array(30)].map((_, i) => (
        <div
          key={i}
          className="absolute bg-indigo-400 rounded-full opacity-70 animate-bounce"
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            width: `${Math.random() * 15 + 5}px`,
            height: `${Math.random() * 15 + 5}px`,
            animationDuration: `${Math.random() * 5 + 3}s`,
          }}
        ></div>
      ))}

      {/* Content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center text-center px-6">
        <h1 className="text-2xl md:text-4xl font-bold text-gray-800 leading-snug">
          Visualize your patterns.{" "}
          <span className="text-indigo-600">
            Built for neurodivergent minds.
          </span>
        </h1>

        <p className="mt-4 max-w-2xl text-gray-600 text-sm md:text-base">
          Track your habits and mood on a grid made for your brain — not for
          dopamine. See what fuels you. Fix what drains you.
        </p>

        <div className="mt-6 flex flex-col sm:flex-row gap-4">
          <button className="px-6 py-3 bg-indigo-600 text-white rounded-lg shadow-md hover:bg-indigo-700 transition">
            Visualize Now
          </button>

          <Link to="/login">
            <button className="px-6 py-3 border border-gray-400 text-gray-700 rounded-lg hover:bg-gray-100 transition">
              See Demo
            </button>
          </Link>
        </div>

        <Link
          to="/blog"
          className="mt-6 text-indigo-600 hover:underline text-sm md:text-base"
        >
          Read our blog →
        </Link>
      </div>
    </div>
  );
}

export default ParticleBackground;
