import {  useNavigate } from 'react-router-dom';
function Component4() {
    const navigate = useNavigate();

    const handleStart = () => {
        const token = localStorage.getItem("token");
        if (token) {
            navigate("/seedemo");  //already loggedIn user → dashboard
        } else {
            navigate("/login");      // not loggedIn user → login page
        }
    };
    return (<>
        <div className="component1-container" style={{ height: "auto" }}>
            <div className="container p-5">
                <div className="row text-center mt-5 mb-5 p-5">
                    <h1 className="fs-2">Transform your daily routines</h1>
                    <p className="fs-5">Join thousands of people who have improved their productivity, health, and well-being with Habitualy.</p>
                    <p className="fs-6"><i class="fa-solid fa-check m-2 fs-5"></i>Start for free
                        <i class="fa-solid fa-check m-2 fs-5 "></i>No credit card required
                        <i class="fa-solid fa-check m-2 fs-5"></i>Privacy-focused
                    </p>
                    <div >
                        <button class="btn btn-lg btn btn-success" onClick={handleStart}>
                            Start for Free
                        </button>
                    </div>
                </div>
            </div>
        </div >
    </>);
}

export default Component4;
