// src/components/Charts/WorkoutChart.js
import { useState, useEffect } from "react";
import { BarChart, Bar, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from "recharts";
import api from "../../utils/api";
import { CustomTooltip } from "../../utils/CustomTooltip.js";

function WorkoutChart({ days = 7, goal = 30 }) {
  const [data, setData] = useState([]);
  const [todayTotal, setTodayTotal] = useState(0);
  const [avg, setAvg] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await api.get("/dailylog");
        const formatted = res.data.map((log) => {
          const totalDuration =
            log.workouts?.reduce((sum, w) => sum + (w.duration || 0), 0) || 0;
          return {
            date: new Date(log.date).toLocaleDateString(),
            duration: totalDuration,
          };
        });

        // sirf last N days ka data lena
        const recent = formatted.slice(-days);
        setData(recent);

        // today ka total
        const todayStr = new Date().toLocaleDateString();
        const todayLog = recent.find((d) => d.date === todayStr);
        setTodayTotal(todayLog?.duration || 0);

        // average
        const total = recent.reduce((sum, d) => sum + d.duration, 0);
        setAvg(recent.length > 0 ? (total / recent.length).toFixed(1) : 0);

      } catch (err) {
        console.error("Error fetching workout data:", err);
      }
    };

    fetchData();
  }, [days]);

  return (
    <div style={{ maxWidth: 700, margin: "20px auto", background: "#fef3c7", borderRadius: 12, padding: 16 }}>
      <h2 style={{ marginBottom: 8, color: "#b45309" }}>
        <i className="fa-solid fa-dumbbell"></i> Workouts
      </h2>
      <div style={{ display: "flex", gap: 16, marginBottom: 16 }}>
        <div><b>Today:</b> {todayTotal}min</div>
        <div><b>Avg ({days}d):</b> {avg}min</div>
        <div><b>Goal:</b> {goal}min/day</div>
      </div>
      <div style={{ width: "100%", height: 300 }}>
        <ResponsiveContainer>
          <BarChart data={data}>
            <CartesianGrid stroke="#fde68a" />
            <XAxis dataKey="date" />
            <YAxis allowDecimals={false} />
            <Tooltip content={<CustomTooltip unit="min" />} />
            <Bar dataKey="duration" fill="#f59e0b" />
            {/* Reference line for dynamic goal */}
            <Line type="monotone" dataKey={() => goal} stroke="#b45309" strokeWidth={2} dot={false} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default WorkoutChart;
