import { Line, CartesianGrid, XAxis, YAxis, Tooltip, ResponsiveContainer, BarChart, Bar } from "recharts";
import { useChartData } from "../../Hook/useChartData";
import { CustomTooltip } from "../../utils/CustomTooltip.js";

// Reusable Chart Card
function ChartCard({ title, iconClass, color, today, avg, unit, children }) {
  return (
    <div className={`max-w-xl mx-auto my-5 p-5 rounded-xl bg-${color}-100 shadow-md`}>
      <h2 className={`mb-2 text-xl font-semibold text-${color}-700`}>
        <i className={`${iconClass} mr-2`}></i> {title}
      </h2>
      <div className="flex gap-6 mb-4">
        <div><b>Today:</b> {today} {unit}</div>
        <div><b>Avg:</b> {avg} {unit}</div>
      </div>
      <div className="w-full h-72">{children}</div>
    </div>
  );
}

// Water Chart
function WaterChart({ days = 7 }) {
  const { data, todayTotal, avg } = useChartData("water", "water", days);

  return (
    <ChartCard
      title="Water Intake"
      iconClass="fa-solid fa-droplet"
      color="blue"
      today={todayTotal}
      avg={avg}
      unit="glasses"
    >
      <ResponsiveContainer>
        <BarChart data={data}>
          <CartesianGrid stroke="#bfdbfe" />
          <XAxis dataKey="date" />
          <YAxis allowDecimals={false} />
          <Tooltip content={<CustomTooltip unit="glasses" />} />
          <Bar dataKey="water" fill="#458bfc" />
          <Line type="monotone" dataKey={() => 8} stroke="#1d4ed8" strokeWidth={2} dot={false} />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );
}

// Calories Chart
function CaloriesChart({ days = 7 }) {
  const { data, todayTotal, avg } = useChartData("calories", "calories", days);

  return (
    <ChartCard
      title="Calories Burned"
      iconClass="fa-solid fa-fire"
      color="red"
      today={todayTotal}
      avg={avg}
      unit="kcal"
    >
      <ResponsiveContainer>
        <BarChart data={data}>
          <CartesianGrid stroke="#fecaca" />
          <XAxis dataKey="date" />
          <YAxis allowDecimals={false} />
          <Tooltip content={<CustomTooltip unit="kcal" />} />
          <Bar dataKey="calories" fill="#ef4444" />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );
}

// Steps Chart
function StepsChart({ days = 7 }) {
  const { data, todayTotal, avg } = useChartData("steps", "steps", days);

  return (
    <ChartCard
      title="Steps"
      iconClass="fa-solid fa-shoe-prints"
      color="green"
      today={todayTotal}
      avg={avg}
      unit="steps"
    >
      <ResponsiveContainer>
        <BarChart data={data}>
          <CartesianGrid stroke="#a7f3d0" />
          <XAxis dataKey="date" />
          <YAxis allowDecimals={false} />
          <Tooltip content={<CustomTooltip unit="steps" />} />
          <Bar dataKey="steps" fill="#089a69" />
          <Line type="monotone" dataKey={() => 12000} stroke="#047857" strokeWidth={2} dot={false} />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );
}

// Sleep Chart
function SleepChart({ days = 7 }) {
  const { data, todayTotal, avg } = useChartData("sleep", "sleep", days);

  return (
    <ChartCard
      title="Sleep"
      iconClass="fa-solid fa-bed"
      color="purple"
      today={todayTotal}
      avg={avg}
      unit="hours"
    >
      <ResponsiveContainer>
        <BarChart data={data}>
          <CartesianGrid stroke="#ddd6fe" />
          <XAxis dataKey="date" />
          <YAxis allowDecimals={false} />
          <Tooltip content={<CustomTooltip unit="hours" />} />
          <Bar dataKey="sleep" fill="#8b5cf6" />
          <Line type="monotone" dataKey={() => 8} stroke="#7c3aed" strokeWidth={2} dot={false} />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );
}

// Distance Chart
function DistanceChart({ days = 7 }) {
  const { data, todayTotal, avg } = useChartData("distance", "distance", days);

  return (
    <ChartCard
      title="Distance"
      iconClass="fa-solid fa-route"
      color="emerald"
      today={todayTotal}
      avg={avg}
      unit="km"
    >
      <ResponsiveContainer>
        <BarChart data={data}>
          <CartesianGrid stroke="#d1fae5" />
          <XAxis dataKey="date" />
          <YAxis allowDecimals={false} />
          <Tooltip content={<CustomTooltip unit="km" />} />
          <Bar dataKey="distance" fill="#10b981" />
          <Line type="monotone" dataKey={() => 5} stroke="#047857" strokeWidth={2} dot={false} />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );
}

export { WaterChart, CaloriesChart, StepsChart, SleepChart, DistanceChart };
