import React, { useState } from 'react';
import api from '../utils/api.js';
import {Link,useNavigate} from 'react-router-dom';

function Login() {
    const [form, setform] = useState({ email: "", password: "" });
     const navigate = useNavigate(); 
    const handleChange = (e) => {
        setform({ ...form, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post("/users/login", form);
            localStorage.setItem("token", res.data.token);
            alert('Login Successful!');
            navigate("/");
        } catch (error) {
            if (error.response && error.response.data.message) {
                alert(error.response.data.message);
            } else {
                alert("Something went wrong!");
            }
        }
    };
    return (
        <>
            <div className="container mx-auto mt-10 mb-10">
    <div className="flex justify-center">
        <div className="w-full md:w-1/3">

            {/* Login Form */}
            <form onSubmit={handleSubmit} className="login-backgoround p-8 rounded-xl shadow-lg bg-white">
                {/* Heading */}
                <h1 className="text-center font-semibold text-2xl">Welcome Back</h1>
                <p className="text-center text-gray-500 mt-1 mb-4 text-sm">
                    Sign in to continue your journey
                </p>
                <hr className="mb-4"/>

                {/* Email */}
                <label htmlFor="emailAddress" className="block mb-1 font-medium">Email address</label>
                <input
                    name='email'
                    placeholder='Email'
                    onChange={handleChange}
                    className="form-control w-full border border-gray-300 rounded-md px-3 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    id="emailAddress"
                    required
                />

                {/* Password */}
                <label htmlFor="userpassword" className="block mb-1 font-medium">Enter Password</label>
                <input
                    type='password'
                    name='password'
                    placeholder='Password'
                    onChange={handleChange}
                    className="form-control w-full border border-gray-300 rounded-md px-3 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
                    id='userpassword'
                    required
                />

                {/* Submit */}
                <div className="grid">
                    <button type='submit' className="btn btn-success bg-green-600 hover:bg-green-700 text-white py-2 rounded-md">
                        Login
                    </button>
                </div>

                {/* Footer */}
                <p className="text-center text-gray-500 mt-3 text-sm">
                    Don't have an Account?
                    <Link to={"/register"} className="underline ml-1">
                        Register
                    </Link>
                </p>
            </form>

        </div>
    </div>
</div>

        </>
    );
}

export default Login;
