import React from "react";
import { Link } from "react-router-dom";
import "./particleBackground.css"; // tujhne bheja hua CSS as is rakhenge

function ParticleBackground() {
  return (
    <div className="particles-container bg-white relative">
      {/* Particles */}
      {[...Array(30)].map((_, i) => (
        <div key={i} className="particle"></div>
      ))}

      {/* Content */}
      <div className="content">
        <h1 className="text-3xl md:text-5xl font-semibold text-gray-900">
          Visualize your patterns.{" "}
          <span className="text-indigo-600">
            Built for neurodivergent minds.
          </span>
        </h1>

        <p className="mt-4 max-w-2xl mx-auto text-gray-700 text-base md:text-lg">
          Track your habits and mood on a grid made for your brain — not for
          dopamine. See what fuels you. Fix what drains you.
        </p>

        <div className="mt-6 flex flex-col sm:flex-row justify-center gap-4">
          <button className="home-btn1 px-6 py-3 rounded-lg shadow-md">
            Visualize Now
          </button>

          <Link to="/login">
            <button className="home-btn2 px-6 py-3 border border-gray-300 rounded-lg">
              See Demo
            </button>
          </Link>
        </div>

        <Link
          to="/blog"
          className="mt-6 inline-block text-indigo-600 hover:underline text-sm md:text-base"
        >
          Read our blog →
        </Link>
      </div>
    </div>
  );
}

export default ParticleBackground;
