import { useState } from "react";
import { WaterChart, CaloriesChart, StepsChart, SleepChart, DistanceChart } from "../components/Charts/fitnessCharts.js";
import { SugarChart, BPChart, HeartRateChart, CholesterolChart, OxygenChart } from "../components/Charts/MedicalCharts.js";
import WorkoutsChart from "../components/Charts/WorkoutsChart.js";

export default function Dashboard() {
  // Step 1: state for dynamic filter
  const [days, setDays] = useState(14);

  return (
    <div className="min-h-screen bg-gray-50 px-6 py-10">
  {/* Heading */}
  <h1 className="text-3xl font-bold text-gray-800 mb-6">Welcome to Dashboard</h1>

  {/* Filter Buttons */}
  <div className="mb-6 flex flex-wrap gap-3">
    <button
      className={`px-4 py-2 rounded-lg font-medium shadow-sm transition ${
        days === 7
          ? "bg-indigo-600 text-white"
          : "bg-white text-indigo-600 border border-indigo-300 hover:bg-indigo-50"
      }`}
      onClick={() => setDays(7)}
    >
      Last 7 Days
    </button>
    <button
      className={`px-4 py-2 rounded-lg font-medium shadow-sm transition ${
        days === 14
          ? "bg-indigo-600 text-white"
          : "bg-white text-indigo-600 border border-indigo-300 hover:bg-indigo-50"
      }`}
      onClick={() => setDays(14)}
    >
      Last 14 Days
    </button>
    <button
      className={`px-4 py-2 rounded-lg font-medium shadow-sm transition ${
        days === 30
          ? "bg-indigo-600 text-white"
          : "bg-white text-indigo-600 border border-indigo-300 hover:bg-indigo-50"
      }`}
      onClick={() => setDays(30)}
    >
      Last 30 Days
    </button>
  </div>

  {/* Charts Grid */}
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
    {/* Column 1 */}
    <div className="space-y-6">
      <WaterChart days={days} />
      <CaloriesChart days={days} />
      <StepsChart days={days} />
      <WorkoutsChart days={days} goal={60} />
    </div>

    {/* Column 2 */}
    <div className="space-y-6">
      <SleepChart days={days} />
      <DistanceChart days={days} />
      <SugarChart days={days} />
      <BPChart days={days} />
    </div>

    {/* Column 3 */}
    <div className="space-y-6">
      <HeartRateChart days={days} />
      <CholesterolChart days={days} />
      <OxygenChart days={days} />
    </div>
  </div>
</div>

  );
}
