import React from "react";
import "./particleBackground.css";

function ParticleBackground() {
  const particles = Array.from({ length: 30 }, (_, i) => ({
    id: i,
    size: Math.floor(Math.random() * 25) + 10, // 10px - 35px
    opacity: Math.random() * 0.6 + 0.3,       // 0.3 - 0.9
  }));

  return (
    <div className="particles-container relative bg-white">
      {particles.map((p) => (
        <div
          key={p.id}
          className="particle"
          style={{
            width: `${p.size}px`,
            height: `${p.size}px`,
            opacity: p.opacity,
          }}
        ></div>
      ))}

      <div className="content">
        <h1 className="text-3xl md:text-5xl font-semibold text-gray-900">
          Visualize your patterns.{" "}
          <span className="text-indigo-600">Built for neurodivergent minds.</span>
        </h1>
        <p className="mt-4 max-w-2xl mx-auto text-gray-700 text-base md:text-lg">
          Track your habits and mood on a grid made for your brain â€” not for dopamine.
        </p>
      </div>
    </div>
  );
}

export default ParticleBackground;
