import React, { useState } from "react";

const WorkoutList = ({ workouts, setWorkouts }) => {
  const [workoutType, setWorkoutType] = useState("");
  const [duration, setDuration] = useState("");
  const [caloriesBurned, setCaloriesBurned] = useState("");

  const addWorkout = () => {
    if (!workoutType || !duration) return;

    const newWorkout = {
      type: workoutType,
      duration: Number(duration),
      caloriesBurned: Number(caloriesBurned) || 0,
    };

    setWorkouts([...workouts, newWorkout]);

    // Reset inputs
    setWorkoutType("");
    setDuration("");
    setCaloriesBurned("");
  };

  const removeWorkout = (index) => {
    const updated = workouts.filter((_, i) => i !== index);
    setWorkouts(updated);
  };

  return (
    <div className="mt-4">
      <h3 className="fs-3 mb-2">Workouts</h3>
      <div className="row">
        <div className="col">
        <select
          value={workoutType}
          onChange={(e) => setWorkoutType(e.target.value)}
          className="form-control"
        >
          <option value="">Select Type</option>
          <option value="Cardio">Cardio</option>
          <option value="Strength">Strength</option>
          <option value="Yoga">Yoga</option>
        </select>
        </div>
        <div className="col">
        <input
          type="number"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
          placeholder="Duration (min)"
          className="form-control"
        />
        </div>
        <div className="col">
        <input
          type="number"
          value={caloriesBurned}
          onChange={(e) => setCaloriesBurned(e.target.value)}
          placeholder="Calories"
          className="form-control"
        />
        </div>
      </div>
      <button
          type="button"
          className="btn btn-sm btn-success mt-3"
          onClick={addWorkout}
        >
          Add
        </button>

      {workouts.length > 0 && (
        <ul className="space-y-1">
          {workouts.map((w, idx) => (
            <li key={idx} className="flex justify-between items-center bg-gray-100 p-2 rounded">
              {w.type} - {w.duration} min - {w.caloriesBurned} cal
              <button
                type="button"
                className="btn btn-sm btn-danger ml-2"
                onClick={() => removeWorkout(idx)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default WorkoutList;
