import React, { useState } from 'react';
import api from '../utils/api.js';
import {Link,useNavigate} from 'react-router-dom';

function Login() {
    const [form, setform] = useState({ email: "", password: "" });
     const navigate = useNavigate(); 
    const handleChange = (e) => {
        setform({ ...form, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post("/users/login", form);
            localStorage.setItem("token", res.data.token);
            alert('Login Successful!');
            navigate("/");
        } catch (error) {
            if (error.response && error.response.data.message) {
                alert(error.response.data.message);
            } else {
                alert("Something went wrong!");
            }
        }
    };
    return (
        <>
            <div className='container mt-5 mb-5' >
                <div className='row'>
                    <div className='col-4'></div>
                    <div className='col-4'>
                        <form onSubmit={handleSubmit} className='login-backgoround p-5'>
                            <h1 className='text-center' style={{ fontWeight: "600" }}>Welcome Back</h1>
                            <p className='fs-6  text-center text-muted'>Sign in to continue your journey</p><hr/>
                            <label for="emailAddress" class="form-label">Email address</label>
                            <input name='email' placeholder='Email' onChange={handleChange} className='form-control' id="emailAddress" required/><br />

                            <label for="userpassword" class="form-label">Enter Password</label>
                            <input type='password' name='password' placeholder='Password' onChange={handleChange} className='form-control' id='userpassword' required/><br />
                            <div class="d-grid">
                                <button type='submit' className='btn btn-success'>Login</button>
                            </div>
                            <p className='fs-6 mt-2 text-center text-muted'>Don't have an Account?
                            <Link to={"/register"} style={{textDecoration:"underline"}}>&nbsp;Register</Link>
                            </p>
                        </form>
                    </div>
                    <div className='col-4'></div>
                </div>
            </div>
        </>
    );
}

export default Login;
