import { useState, useEffect } from "react";
import api from "../utils/api.js";

function Profile() {
  const [form, setForm] = useState({
    name: "",
    age: "",
    height: "",
    weight: "",
    gender: "",
    goal: {
      water: 8,
      steps: 12000,
      sleep: 8,
      calories: 2000
    }
  });

  const token = localStorage.getItem("token");

  // Fetch profile when page loads
  useEffect(() => {
    const fetchProfile = async () => {
      const res = await api.get("/users/profile", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({
        ...res.data,
        goal: res.data.goal || {
          water: 8,
          steps: 12000,
          sleep: 8,
          calories: 2000
        }
      });
    };
    fetchProfile();
  }, [token]);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: Number(e.target.value) });
  };

  // Handle goal input
  const handleGoalChange = (e) => {
    setForm({
      ...form,
      goal: { ...form.goal, [e.target.name]: e.target.value }
    });
  };

  // Update profile
  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await api.put("/users/profile", form, {
      headers: { Authorization: `Bearer ${token} ` }
    });
    setForm(res.data);
    alert("Profile updated successfully!");
  };

  return (
    <div className="container mt-5 mb-5">
      <div className="row">
        <div className="col-3"></div>
        <div className="col-6">
          <form onSubmit={handleSubmit} className='login-backgoround p-4'>
            <h1 className='text-center' style={{ fontWeight: "600" }}>Updated Profile</h1><hr />
            <input name="name" value={form.name} placeholder="Name" className='form-control mt-4' onChange={handleChange} />
            <input name="age" value={form.age} placeholder="Age" className='form-control mt-4' onChange={handleChange} />
            <div className="row">
              <div className="col">
                <input name="height" value={form.height} placeholder="Height (cm)" className='form-control mt-4' onChange={handleChange} />
              </div>
              <div className="col">
                <input name="weight" value={form.weight} placeholder="Weight (kg)" className='form-control mt-4' onChange={handleChange} />
              </div>
            </div>
            <input name="gender" value={form.gender} placeholder="Gender" className='form-control mt-4' onChange={handleChange} />
            <h4 className="mt-4"><i class="fa-solid fa-bullseye"></i> Goals</h4>
            <div className="row">
              <div className="col">
                <input
                  name="water"
                  value={form.goal.water}
                  placeholder="Water (glasses)"
                  className="form-control mt-2"
                  onChange={handleGoalChange}
                />
              </div>
              <div className="col">
                <input
                  name="steps"
                  value={form.goal.steps}
                  placeholder="Steps"
                  className="form-control mt-2"
                  onChange={handleGoalChange}
                />
              </div>
            </div>
            <div className="row">
              <div className="col">
                <input
              name="sleep"
              value={form.goal.sleep}
              placeholder="Sleep (hours)"
              className="form-control mt-2"
              onChange={handleGoalChange}
            />
              </div>
              <div className="col">
                <input
              name="calories"
              value={form.goal.calories}
              placeholder="Calories (kcal)"
              className="form-control mt-2"
              onChange={handleGoalChange}
            />
              </div>
            </div>
            <button type="submit" className="btn btn-info mt-4">Update Profile</button>
          </form>
        </div>
        <div className="col-3"></div>
      </div>
    </div>
  );
}

export default Profile;
