import { useState } from 'react';
import api from '../utils/api.js';
import { Link, useNavigate } from 'react-router-dom';

function Register() {
    const [form, setform] = useState({ name: "", email: "", password: "" });
    const navigate = useNavigate();
    const handleChange = (e) => {
        setform({ ...form, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post("/users/register", form);
            localStorage.setItem('token', res.data.token);
            alert('User Registerd Successfully!');
            navigate("/login");
        } catch (error) {
            if (error.response && error.response.data.message) {
                alert(error.response.data.message);
            } else {
                alert("Something went wrong!");
            }
        }
    }
    return (
        <>
            <div className='container mt-5 mb-5'>
                <div className='row'>
                    <div className='col'></div>
                    <div className='col'>
                        <form onSubmit={handleSubmit} className='login-backgoround p-5'>
                            <h1 className='text-center' style={{ fontWeight: "600" }}>Register</h1><hr />
                            <input name='name' placeholder='Name' className='form-control mt-4' onChange={handleChange} required />
                            <input name='email' placeholder='Email' className='form-control mt-4' onChange={handleChange} required />
                            <input type='password' name='password' placeholder='Password' className='form-control mt-4' onChange={handleChange} required />
                            <div class="d-grid mt-5">
                                <button type='submit' className='btn btn-success'>Register</button>
                            </div>
                            <p className='fs-6 mt-3 text-center text-muted'>Already have an Account?
                                <Link to={"/login"} style={{ textDecoration: "underline" }}>&nbsp;LogIn</Link>
                            </p>
                        </form>
                    </div>
                    <div className='col'></div>
                </div>
            </div>
        </>
    );
}

export default Register;
