import { useState, useEffect } from "react";
import api from "../utils/api.js";

function Profile() {
  const [form, setForm] = useState({
    name: "",
    age: "",
    height: "",
    weight: "",
    gender: "",
    goal: {
      water: 8,
      steps: 12000,
      sleep: 8,
      calories: 2000
    }
  });

  const token = localStorage.getItem("token");

  // Fetch profile when page loads
  useEffect(() => {
    const fetchProfile = async () => {
      const res = await api.get("/users/profile", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({
        ...res.data,
        goal: res.data.goal || {
          water: 8,
          steps: 12000,
          sleep: 8,
          calories: 2000
        }
      });
    };
    fetchProfile();
  }, [token]);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: Number(e.target.value) });
  };

  // Handle goal input
  const handleGoalChange = (e) => {
    setForm({
      ...form,
      goal: { ...form.goal, [e.target.name]: e.target.value }
    });
  };

  // Update profile
  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await api.put("/users/profile", form, {
      headers: { Authorization: `Bearer ${token} ` }
    });
    setForm(res.data);
    alert("Profile updated successfully!");
  };

  return (
    <div className="flex justify-center items-center mt-10 mb-10">
      <div className="w-full max-w-2xl">
        <form
          onSubmit={handleSubmit}
          className="bg-white shadow-lg rounded-2xl p-6"
        >
          <h1 className="text-center text-2xl font-semibold text-gray-800">
            Updated Profile
          </h1>
          <hr className="my-4" />

          {/* Basic Info */}
          <input
            name="name"
            value={form.name}
            placeholder="Name"
            className="w-full border rounded-lg px-4 py-2 mt-4 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
            onChange={handleChange}
          />
          <input
            name="age"
            value={form.age}
            placeholder="Age"
            className="w-full border rounded-lg px-4 py-2 mt-4 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
            onChange={handleChange}
          />

          <div className="grid grid-cols-2 gap-4">
            <input
              name="height"
              value={form.height}
              placeholder="Height (cm)"
              className="w-full border rounded-lg px-4 py-2 mt-4 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleChange}
            />
            <input
              name="weight"
              value={form.weight}
              placeholder="Weight (kg)"
              className="w-full border rounded-lg px-4 py-2 mt-4 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleChange}
            />
          </div>

          <input
            name="gender"
            value={form.gender}
            placeholder="Gender"
            className="w-full border rounded-lg px-4 py-2 mt-4 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
            onChange={handleChange}
          />

          {/* Goals */}
          <h4 className="mt-6 text-lg font-semibold text-gray-700 flex items-center gap-2">
            <i className="fa-solid fa-bullseye text-indigo-600"></i> Goals
          </h4>

          <div className="grid grid-cols-2 gap-4">
            <input
              name="water"
              value={form.goal.water}
              placeholder="Water (glasses)"
              className="w-full border rounded-lg px-4 py-2 mt-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleGoalChange}
            />
            <input
              name="steps"
              value={form.goal.steps}
              placeholder="Steps"
              className="w-full border rounded-lg px-4 py-2 mt-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleGoalChange}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <input
              name="sleep"
              value={form.goal.sleep}
              placeholder="Sleep (hours)"
              className="w-full border rounded-lg px-4 py-2 mt-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleGoalChange}
            />
            <input
              name="calories"
              value={form.goal.calories}
              placeholder="Calories (kcal)"
              className="w-full border rounded-lg px-4 py-2 mt-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              onChange={handleGoalChange}
            />
          </div>

          {/* Button */}
          <button
            type="submit"
            className="w-full bg-indigo-500 text-white font-semibold py-2 rounded-lg mt-6 hover:bg-indigo-600 transition"
          >
            Update Profile
          </button>
        </form>
      </div>
    </div>

  );
}

export default Profile;
